// MatrimonySearchPage.js
import React, { useState } from 'react';
import {
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Box,
  Typography,
} from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    // padding: theme.spacing(3),
  },
  formControl: {
    // margin: theme.spacing(1),
    minWidth: 120,
  },
  button: {
    // marginTop: theme.spacing(2),
  },
}));

const MatrimonySearchPage = () => {
  const classes = useStyles();
  const [gender, setGender] = useState('');
  const [age, setAge] = useState('');
  const [location, setLocation] = useState('');

  const handleGenderChange = (event) => {
    setGender(event.target.value);
  };

  const handleAgeChange = (event) => {
    setAge(event.target.value);
  };

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const handleSearch = () => {
    // Implement your search logic here
    // You can use the 'gender', 'age', and 'location' state values to filter data
  };

  return (
    <Box className={classes.root}>
      <Typography variant="h5" gutterBottom>
        Search and Filter
      </Typography>
      <FormControl className={classes.formControl}>
        <InputLabel>Gender</InputLabel>
        <Select value={gender} onChange={handleGenderChange}>
          <MenuItem value="male">Male</MenuItem>
          <MenuItem value="female">Female</MenuItem>
        </Select>
      </FormControl>
      <br></br>
      <FormControl className={classes.formControl}>
        <InputLabel>Age</InputLabel>
        <Select value={age} onChange={handleAgeChange}>
          <MenuItem value="18-25">18-25</MenuItem>
          <MenuItem value="26-35">26-35</MenuItem>
          <MenuItem value="36-50">36-50</MenuItem>
        </Select>
      </FormControl>
      <br></br>
      <FormControl className={classes.formControl}>
        <InputLabel>Location</InputLabel>
        <Select value={location} onChange={handleLocationChange}>
          <MenuItem value="city1">City 1</MenuItem>
          <MenuItem value="city2">City 2</MenuItem>
          <MenuItem value="city3">City 3</MenuItem>
        </Select>
      </FormControl>
      <br></br>
      <Button
        className={classes.button}
        variant="contained"
        color="primary"
        onClick={handleSearch}
      >
        Search
      </Button>
    </Box>
  );
};

export default MatrimonySearchPage;
