  const navigate = useNavigate();
  const [name,setName]=useState('');
 
  const [email,setEmail]=useState('');

  const [password,setPassword]=useState('');
  const handleSubmit= async(e)=>{
    e.preventDefault();
    try{
      const response=await axios.post('http://localhost:8181/api/v1/auth/register',{name,email,password});
      if(response.status==200){
        navigate('/home');
        setName('');
        setPassword('');
        setEmail('');
      }
    }
    catch(error){
      console.error("Error: " ,error);
    }
  }

  return (
    <>
      <div className="loginnav">
        <Navbar/>
      </div>
    <div className="loginmain">
    <div className="login-container">
      <h2 className="loginhead">Login Form</h2>
      <form onSubmit={handleSubmit} className="logindiv">
        <input
          type="text"
          value={username}
          className="logininput"
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
        />
        <input
          type="password"
          value={password}
          className="logininput"
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
        />
        <button type="submit" className="loginbut">Login</button>
        {errorText && <p className="error-text">{errorText}</p>}
      </form>
    </div>
    </div>
    </>
  );
};

export default LoginForm;
