import React, { useState } from "react";
import axios from "axios";
import "./login.css";
import Navbar from "../homepage/nav";
import { useNavigate } from 'react-router-dom';
import { useDispatch } from "react-redux";

const LoginForm = () => {
  
  const [pname, setPname] = useState('');
  const dispatch = useDispatch();
  
  
  const handleChange = (e) => {
    setPname(e.target.value);
  }
  const [name,setName]=useState('');
  
  const [email,setEmail]=useState('');
  
  const [password,setPassword]=useState('');
  const validateEmail = (email) => {
    
    const pattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    
    return pattern.test(email);
  }
  const navigate = useNavigate();
  // dispatch(setName({name2:name}));
  const handleSubmit= async(e)=>{
    e.preventDefault();
    if (!validateEmail(email)) {
      setEmailError('Please enter a valid email address.');
      return;
    }
    try{
      const response=await axios.post('http://localhost:8181/api/v1/auth/authenticate',{name,email,password});
      if(response.status===200){
        navigate('/landing');
        setName('');
        setPassword('');
        setEmail('');
      }
    }
    catch(error){
      console.error("Error: " ,error);
    }
  }
  
  

  return (
    <>
      <div className="loginnav">
        <Navbar/>
      </div>
    <div className="loginmain">
    <div className="login-container">
      <h2 className="loginhead">Login Form</h2>
      <form onSubmit={handleSubmit} className="logindiv">
        <input
          type="text"
          value={name}
          className="logininput"
          onChange={(e) => setName(e.target.value)}
          placeholder="Username"
        />
         <input
          type="text"
          value={email}
          className="logininput"
          onChange={(e) => setEmail(e.target.value)}
          placeholder="EmailId"
        />
        <input
          type="password"
          value={password}
          className="logininput"
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
        />
        <button type="submit" className="loginbut" onClick={() => dispatch(login({ name:email}))}>Login</button>
        {/* {errorText && <p className="error-text">{errorText}</p>} */}
      </form>
    </div>
    </div>
    </>
  );
};

export default LoginForm;
