import React, { useState } from 'react';
import "./profile.css";

const MatrimonyProfile = () => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [location, setLocation] = useState('');
  const [about, setAbout] = useState('');
  const [partnerPreferences, setPartnerPreferences] = useState('');
  const [photoPreview, setPhotoPreview] = useState('');

  const handlePhotoUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      setPhotoPreview(e.target.result);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Process the form submission or save the data
    // Here, you can make an API call to send the form data to a server or perform any desired action
    console.log('Form submitted:', {
      name,
      age,
      gender,
      location,
      about,
      partnerPreferences,
      photoPreview,
    });
  };

  return (
    <div className="container">
      <h1>Matrimony Profile</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />

        <label htmlFor="age">Age:</label>
        <input
          type="text"
          id="age"
          name="age"
          value={age}
          onChange={(e) => setAge(e.target.value)}
          required
        />

        <label htmlFor="gender">Gender:</label>
        <select
          id="gender"
          name="gender"
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          required
        >
          <option value="">-- Select --</option>
          <option value="male">Male</option>
          <option value="female">Female</option>
        </select>

        <label htmlFor="location">Location:</label>
        <input
          type="text"
          id="location"
          name="location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          required
        />

        <label htmlFor="about">About Me:</label>
        <textarea
          id="about"
          name="about"
          value={about}
          onChange={(e) => setAbout(e.target.value)}
        ></textarea>

        <label htmlFor="partner">Partner Preferences:</label>
        <textarea
          id="partner"
          name="partner"
          value={partnerPreferences}
          onChange={(e) => setPartnerPreferences(e.target.value)}
        ></textarea>

        <div className="photo-upload">
          <label htmlFor="photo">Upload Photo:</label>
          <input
            type="file"
            id="photo"
            name="photo"
            onChange={handlePhotoUpload}
          />
          <img
            id="photo-preview"
            src={photoPreview}
            alt="Photo Preview"
          />
        </div>

        <div className="clearfix">
          <button type="submit">Save</button>
        </div>
      </form>
    </div>
  );
};

export default MatrimonyProfile;
